      set:(nz+1)<=lava("maxz") $list(q,*+1) = $listbuild(nx,ny,nz+1)
      set:(nz-1)>=lava("minz") $list(q,*+1) = $listbuild(nx,ny,nz-1)
    }
  }

  // count exposed facets of solid = -air
  set solid = 0
  for x=lava("minx"):1:lava("maxx") {
