          s node = matcher.Group(1)
          s left = matcher.Group(2)
          s op = matcher.Group(3)
          s right = matcher.Group(4)
                 
          if right="" {
            s t(node,"value") = left+0
          } else {
           s t(node,"op") = op
           s t(node,"left") = left
           s t(node,"right") = right
          }                    
        } else {
          throw ##class(%Exception.General).%New("invalid input")
        }
      }
  } catch (ex) {
    s sc = ex.AsStatus()
  }
  q sc

