              set:pcol<grid("minCol") grid("minCol")=pcol
              set:pcol>grid("maxCol") grid("maxCol")=pcol
            }
            set pcol = $order(proposals(prow,pcol))
          }
          set prow = $order(proposals(prow))
        }
        set rotation = $list(rotation,2,*)_$list(rotation,1,1)

        return moves

