#Dim sc as %Status #Dim ex as %Exception.AbstractException
#Dim line as %String
set sc = $$$OK try { set hx=0 set hy=0 set tx=0 set ty=0 set tot=1 set grid(tx,ty)=1
while 'inputStream.AtEnd { set line = inputStream.ReadLine(,.sc) set line = $zstrip(line,"<>C") set dir = $piece(line," ",1) set steps = $piece(line," ",2) set ox = $case(dir,"L":-1,"R":1,:0) set oy = $case(dir,"U":1,"D":-1,:0)
for i=1:1:steps {
set hx=hx+ox set hy=hy+oy do ..MoveNode(hx,hy,.tx,.ty) if '$get(grid(tx,ty),0) { set tot = tot+1 set grid(tx,ty)=1 } } } //d ..PrintGrid(.grid,hx,hy,tx,ty) write "Part 1 answer : ",tot,! } catch (ex) { set sc = ex.AsStatus() } return sc

    #Dim line as %String
    #Dim len as %Integer

    set sc = $$$OK
    try {
        set hx=0
        set hy=0
        
        set len=9
        for i = 1:1:len {
          set tx(i) = 0
          set ty(i) = 0
        }
        set tot=1
        set grid(0,0)=1

      while 'inputStream.AtEnd {
        set line = inputStream.ReadLine(,.sc)
        set line = $zstrip(line,"<>C")
        set dir = $piece(line," ",1)
        set steps = $piece(line," ",2)
        set ox = $case(dir,"L":-1,"R":1,:0)
        set oy = $case(dir,"U":1,"D":-1,:0)
        for i=1:1:steps {
        set hx=hx+ox
        set hy=hy+oy

        set nx=hx
        set ny=hy

        for j=1:1:len {
         do ..MoveNodeArr(nx,ny,.tx,.ty,j)
         set nx = tx(j)
         set ny = ty(j)
        }

        if '$get(grid(tx(len),ty(len)),0) {
          set tot = tot+1
          set grid(tx(len),ty(len))=1
        }
        }
      }
      //d ..PrintGrid(.grid,hx,hy,tx,ty)
      write "Part 2 answer : ",tot,!
    }
    catch (ex) {
      set sc = ex.AsStatus()
    }
    return sc
